package uppgift1;

import java.io.BufferedReader;
import java.io.FileNotFoundException;
import java.io.FileReader;
import java.io.IOException;

public class NaiveBruteForce {

	public static int search(String pat, String txt) {
		int M = pat.length();
		int N = txt.length();
		for (int i = 0; i <= N - M; i++) {
			int j;
			for (j = 0; j < M; j++)
				if (txt.charAt(i + j) != pat.charAt(j))
					break;
			if (j == M)
				return i; // found
		}
		return N; // not found
	}

	public static void main(String[] args) throws IOException {
		String testPat1 = "AAAAAB";
		String testTxt1 = "AAAAAAAAAB";
		String testPat2 = "AAAAAAAAAAAAAAAAAAAAAB";
		String testTxt2 = "AAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAB";
		String testPat3 = "AAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAB";
		String testTxt3 = "AAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAB";
		
		String testPat4 = "";
		
		//7604 tecken
		String testTxt4 = "";
		String testPat5 = "ot";
		String testTxt5 = "ghuehurehthiyerojreyijyuehrturothtuthreuhuenwfwebriewruiewhr";
		String testPat6 = "B";
		
		String biblePattern,bible;

		BufferedReader br = new BufferedReader(new FileReader("files/bible-en.txt"));
		try {
			StringBuilder sb = new StringBuilder();
			String line = br.readLine();

			while (line != null) {
				sb.append(line);
				sb.append(System.lineSeparator());
				line = br.readLine();
			}
			bible = sb.toString();
			biblePattern = bible.substring(50000, 99999);
		} finally {
			br.close();
		}

		long before = System.currentTimeMillis();
		System.out.println(search(biblePattern, bible));
		long after = System.currentTimeMillis();
		System.out.println((after - before) + " millisekunder");
	}

}
